image.onUp:
__ht__function(event, data, view, point, width, height) {
if(!data.ca('disabled')){
    data.ca('icon-background',data.ca('hover-background'));
    let cb = new Function('return ' + data.ca('clicked'))(),
        cb2 = new Function('return ' + data.ca('onClick'))();//240523，兼容onClick命名
    typeof(setBindTabviewIndex) != 'undefined' && setBindTabviewIndex(data,data.ca('bindTabview'));
    let paramCb = new Function('return ' + data.ca('param'))();
    let param = paramCb ? paramCb(data,view) : null;
    if(typeof(i) != 'undefined' && typeof(i.updateBindControls) != 'undefined'){
        //240304，需要改成i.formEventBubblingUpper，否则执行操作可能会出问题！比如tab页签索引切换，切换不了！
        // i.updateBindControls(data,param,[],false,'~','clicked');//params是response的位置，所以得是对象类型\
        //触发事件连线操作
        i.formEventBubblingUpper(
            data,
            data._gv,
            data._cache,
            'clicked',
            null,
            false
        );
    }
    //230819，支持传入url，点击浏览器新窗口打开。
    let urltmp = data.ca('openUrl');
    if(urltmp && urltmp.indexOf('http') == -1) urltmp = 'http://' + urltmp;
    urltmp && window.open(urltmp,'_blank');
    
    if(cb2) return cb2(data,param);
    else return cb && cb(data,param)
}
}

