            typeArr.forEach((curType,index)=>{
                if(curType != typetmp){        //路径中先前的类型名称换成新的
                    path = path.replace(curType,typetmp)               
                }
            })
        }
    }
    data.ca('path',path)                      //回写过去
    return path
}

